from tkinter import *from tkmacosx import *root = Tk()root.title("Aski-value")root.geometry("400x400")root.configure(background="aqua")text_input = Entry(root)text_input.place(relx = 0.5 , rely = 0.4 , anchor = CENTER)label = Label(root , text = "Ascii Value: " , bg = "red" , fg = "black")label.place(relx = 0.5 , rely = 0.5 , anchor = CENTER)label_2 = Label(root , bg = "red" , fg = "black")label_2.place(relx = 0.5 , rely = 0.6 , anchor = CENTER)def ascii_Converter():    input_word = text_input.get()    label["text"] = "Ascii Value: "    label_2["text"] = "Encrypted Value: "    for letter in input_word :        label["text"] += str(ord(letter)) + " , "        asci_value = ord(letter)        encrypted_value = asci_value - 1        encrypted_letter = str(chr(encrypted_value))        label_2["text"] += encrypted_letter                button =Button(root , text = "Show Ascii Value" , bg = "blue" , fg = "black" , command = ascii_Converter)button.place(relx = 0.5 , rely = 0.7 , anchor = CENTER)root.mainloop()